import {
  ActivityIndicator,
  Animated,
  StyleSheet,
  Text,
  View,
} from "react-native";
import React, { useEffect, useRef, useState } from "react";

import styles from "../StylesSheets/stryles";

const CoursePage = ({ navigation }) => {

  const [timeLeft, setTimeLeft] = useState(null);
  let targetTime = 5000;
  let resendTimerInterval;

  const [width, setWidth] = useState(0);
  const animatedValue = useRef(new Animated.Value(-1000)).current
  const reactive = useRef(new Animated.Value(-1000)).current


  const CountTimeLeft = (finalTime) => {
    const difference = finalTime - +new Date();
    if (difference >= 0) {
      setTimeLeft(Math.round(difference / 1000));
    } else {
      clearInterval(resendTimerInterval);
      navigation.navigate("MainPage");
    }
  };

  const TiggerTime = () => {
    console.log(+new Date());
    const finalTime = +new Date() + targetTime;
    resendTimerInterval = setInterval(() => {
      CountTimeLeft(finalTime), 1000;
    });
  };

  useEffect(() => {
    TiggerTime();
    Animated.timing(animatedValue, {
      toValue: reactive,
      duration: 300,
      useNativeDriver: true
    }).start;
  }, []);

  useEffect(() => {
    reactive.setValue(width - (width * timeLeft) / targetTime)
    console.log(width - (width * timeLeft) / targetTime)
  }, [width, timeLeft]);

  return (
    <View style={styles.courseContainer}>
      <Text>{timeLeft}</Text>
      <View
      onLayout={(e) => {
        const newWidth = e.nativeEvent.layout.width
        console.log(newWidth)
        setWidth(newWidth);
      }}
        style={{
          height: 20,
          backgroundColor: "rgba(0,0,0,0.1)",
          borderRadius: 20,
          overflow:'hidden',
        }}
      >
        <Animated.View
          style={{
            height: 20,
            borderRadius: 20,
            width: "100%",
            backgroundColor: "rgba(0,0,0,0.5)",
            position: 'absolute',
            left:0,
            top:0,
            transform: [{
              translateX: animatedValue
            }]
          }}
        />
      </View>
    </View>
  );
};

export default CoursePage;
